import { css } from 'styled-components';

export default css`
  &.o-productDetail {
    position: relative;
    margin: ${props => `-${props.theme.sizes.headerOverlap.small}`} 0 40px;
    padding: ${props => props.theme.sizes.headerOverlap.small} 0
      ${props => props.theme.padding.paddingMedium};
    background-color: ${props => props.backgroundColor || props.theme.colors.BACKGROUND.GREY1};

    @media ${props => props.theme.mediaQuery.medium} {
      margin-top: ${props => `-${props.theme.sizes.headerOverlap.medium}`};
      padding: ${props => props.theme.sizes.headerOverlap.medium} 0 0;
    }
  }
`;
