import React, { Component } from 'react';
import { connect } from 'react-redux';
import { compose } from 'redux';
import initRedux from '../../../../lib/configureStore';
import saga from './SearchDetail.saga';
import productDetailPageData from './SearchDetail.reducer';
import loadProductInfo from './SearchDetail.action';
import styles from './SearchDetail.style';
import withStyles from '../../../../lib/withStyles';

class SearchDetail extends Component {
	constructor() {
		super();
		this.post=[];
		this.state = {
			sortValue: 'ascending',
			searchValue: '',
			post: [],
			searchedArray: [],
		}
	}
	componentDidMount() {
		this.props.showProductsDispatch();
	}

	getUniqueGenderValueofCharacter = characterArray => characterArray && [...new Set(characterArray.map(a => a.gender))];

	getUniqueSpeciesValueofCharacter = characterArray => characterArray && [...new Set(characterArray.map(a => a.species))];

	getUniqueOriginValueofCharacter = characterArray => characterArray && [...new Set(characterArray.map(a => a.origin.name))];

	getFilterValue = (key, filterObject) => {
		return filterObject.map((value) => {
			return (
				<div className="truncate">
					<input type="checkbox" id={`${key}-${value}`} onChange={this.handleFilterChange} name={value} />
					<label for={value} className="search-checkbox">{value}</label><br />
				</div>
			)
		})
	}

	getDateDiffInYears = date => {
		const createdDate = new Date(date);
		const currentDate = new Date();
		return Math.abs(new Date(currentDate-createdDate).getUTCFullYear() - 1970);
	}

	handleChange = e => {
		this.setState({
			sortValue: e.target.value,
		})
	}

	compare = (a, b) => {
		const bandA = a.id;
		const bandB = b.id;

		let comparison = 0;
		if (bandA > bandB) {
			comparison = 1;
		} else if (bandA < bandB) {
			comparison = -1;
		}
		return comparison;
	}

	handleSearchChange = e => {
		this.setState({ searchValue: e.target.value });
	}

	handleClick = () => {
		const { searchValue, post } = this.state;
		const { characters } = this.props;

		const charactersToBeSearched = post.length > 0 && searchValue ? post : characters;

		const searchedPost =[];

		charactersToBeSearched.find((post) => {
			if(post.name.toLowerCase().includes(searchValue.toLowerCase()))
				searchedPost.push(post);
		});

		this.setState({
			post: searchedPost
		})
	}

	getRemovedResult = toDeleteArray => {
		const { post } = this.state;
		const toDelete =  new Set(toDeleteArray.map(item => item.id));
		return post.filter(obj => !toDelete.has(obj.id));
	}

	handleFilterChange = e => {
		const { characters } = this.props;
		const { post } = this.state;
		const key = e.target.id.split('-')[0];

		let uniqueArray;
		let toDeleteArray;
		let newArray;

		if (key.toLowerCase() === 'gender') {
			if (e.target.checked) {
				uniqueArray = new Set(post.concat(characters.filter(item => item.gender === e.target.name)))
				this.setState({
					post: [...uniqueArray],
					searchedArray: [...uniqueArray]
				})
			} else {
				toDeleteArray = characters.filter(item => item.gender === e.target.name);
				newArray = this.getRemovedResult(toDeleteArray);

				this.setState({
					post: [...newArray],
					searchedArray: [...newArray]
				})
			}
		} else if (key.toLowerCase() === 'species') {
			if (e.target.checked) {
				uniqueArray = new Set(post.concat(characters.filter(item => item.species === e.target.name)))
				this.setState({
					post: [...uniqueArray],
					searchedArray: [...uniqueArray]
				})
			} else {
				toDeleteArray = characters.filter(item => item.species === e.target.name);
				newArray = this.getRemovedResult(toDeleteArray)

				this.setState({
					post: [...newArray],
					searchedArray: [...newArray]
				})
			}
		} else {
			if(e.target.checked) {
				uniqueArray = new Set(post.concat(characters.filter(item => item.origin.name === e.target.name)))
				this.setState({
					post: [...uniqueArray],
					searchedArray: [...uniqueArray]
				})
			} else {
				toDeleteArray = characters.filter(item => item.origin.name === e.target.name);
				newArray = this.getRemovedResult(toDeleteArray);

				this.setState({
					post: [...newArray],
					searchedArray: [...newArray]
				})
			}
		}
	}

	render() {
		const { characters, className } = this.props;
		const { sortValue, post } = this.state;

		let posts = post.length > 0 ? post : characters;

		const filterObject = {
			gender: this.getUniqueGenderValueofCharacter(posts),
			species: this.getUniqueSpeciesValueofCharacter(posts),
			origin: this.getUniqueOriginValueofCharacter(posts),
		}

		debugger;

		posts = posts && sortValue === 'ascending' ? posts.sort(this.compare) : posts && posts.reverse();

		return posts && posts.length > 0 ? (
			<div className={`${className} o-searchDetail`}>
				<div className="row">
					<div className="col-md-3 col-xs-12 fc-black">
						<h1>Filters</h1>
						{Object.keys(filterObject).map(key => {
							return (
								<div className="filter-border">
									<div className="filter-keys">
										{key}
									</div>
									{this.getFilterValue(key, filterObject[key])}
								</div>
							)
						})}
					</div>
					<div className="col-md-9 col-xs-12">
						<div className="row search-sort-section">
							<h1 className="col-xs-12 pl-0">Selected Filters</h1>
							<div className="col-xs-6 mt30 pl-0">
								<label for="search" className="search-label">Search By Name</label><br/>
								<input type="text" id="search" name="search" className="search-input" onChange={ this.handleSearchChange } />
								<button type="button" value="Submit" className="search-button" onClick={this.handleClick}>Search</button>
							</div>
							<div className="col-xs-6 mt30 relative">
								<select id="sort" name="sort" className="sort" onChange={this.handleChange}>
									<option value="ascending" selected>Ascending</option>
									<option value="decending">Decending</option>
								</select>
							</div>
						</div>
						<div className="row items">
							{posts.map(character => (
								<div className="character-block col-md-3 col-xs-6">
									<div>
										<img className="full-image" src={character.image} alt="" />
									</div>
									<div className="character-name">
										{character.name}
										<br/>
										<span className="label-size">
											{`id: `}
