import { css } from 'styled-components';

export default css`
  &.o-searchDetail {
    background: ${props => props.theme.colors.primary};
    color: white;

    .character-block {
      background: #696969;
      padding: 15px;
      width: 22%;
      position: relative;
    }

    .character-name {
      position: absolute;
      top: 240px;
      background: black;
      opacity: 0.8;
      padding: 10px;
      width: 100%;
      max-width: 280px;
      font-size: 24px;
    }

    .fs14 {
      font-size: 14px;
    }
  }
`;
