      border-bottom-left-radius: 10px;
      min-height: 250px;
    }

    .character-name {
      position: absolute;
      top: 95px;
      background: black;
      opacity: 0.6;
      padding: 5px 10px;
      width: 100%;
      max-width: 104px;
      font-size: 14px;

      @media only screen and (min-width: 768px)  {
        top: 259px;
        opacity: 0.6;
        padding: 10px;
        max-width: 320px;
        font-size: 24px;
        width: 87%;
      }
