import React, { Component } from 'react';
import { connect } from 'react-redux';
import { compose } from 'redux';
import initRedux from '../../../../lib/configureStore';
import saga from './SearchDetail.saga';
import productDetailPageData from './SearchDetail.reducer';
import loadProductInfo from './SearchDetail.action';
import styles from './SearchDetail.style';
import withStyles from '../../../../lib/withStyles';

class SearchDetail extends Component {
	componentDidMount() {
		this.props.showProductsDispatch();
	}

	getUniqueGenderValueofCharacter = characterArray => characterArray && [...new Set(characterArray.map(a => a.gender))];

	getUniqueSpeciesValueofCharacter = characterArray => characterArray && [...new Set(characterArray.map(a => a.species))];

	getUniqueOriginValueofCharacter = characterArray => characterArray && [...new Set(characterArray.map(a => a.origin.name))];

	getFilterValue = filterObject => {
		return filterObject.map((value) => {
			return (
				<div>
					{value}
				</div>
			)
		})
	}

	getDateDiffInYears = date => {
		const createdDate = new Date(date);
		const currentDate = new Date();
		return Math.abs(new Date(currentDate-createdDate).getUTCFullYear() - 1970);
	}

	render() {
		const { characters, className } = this.props;
		debugger;

		const filterObject = {
			gender: this.getUniqueGenderValueofCharacter(characters),
			species: this.getUniqueSpeciesValueofCharacter(characters),
			origin: this.getUniqueOriginValueofCharacter(characters),
		}

		return characters ? (
			<div className={`${className} o-searchDetail`}>
				<div className="row">
					<div className="col-md-3 col-xs-12 fc-black">
						<h1>Filters</h1>
						{Object.keys(filterObject).map(key => {
							return (
								<div className="filter-border">
									<div className="filter-keys">
										{key}
									</div>
									{this.getFilterValue(filterObject[key])}
								</div>
							)
						})}
					</div>
					<div className="col-md-9 col-xs-12">
						<div className="row search-sort-section">
							<h1 className="col-xs-12">Selected Filters</h1>
							<div className="col-xs-6 mt30">
								<label for="search" className="search-label">Search By Name</label><br/>
								<input type="text" id="search" name="search" className="search-input" />
								<button type="button" value="Submit" className="search-button">Search</button>
							</div>
							<div className="col-xs-6 mt30">
								&nbsp;
							</div>
						</div>
						<div className="row items">
							{characters.map(character => (
								<div className="character-block col-md-3 col-xs-6">
									<div>
										<img className="full-image" src={character.image} alt="" />
									</div>
									<div className="character-name">
										{character.name}
										<br/>
										<span className="label-size">
											{`id: `}
										</span>
										<span className="label-size">
											{`${character.id} - `}
										</span>
										<span className="label-size">
											created {this.getDateDiffInYears(character.created)} years ago
										</span>
									</div>
									<div className="character-detail">
										<div class="row wrap">
											<div className="col-xs-6 adjust-padding-left">
												<span className="details darkgray">Status</span>
											</div>
											<div className="col-xs-6 align-right adjust-padding-right">{character.status}</div>
										</div>
										<div class="row wrap">
											<div className="col-xs-6 adjust-padding-left">
												<span className="details darkgray">Species</span>
											</div>
											<div className="col-xs-6 align-right adjust-padding-right">{character.species}</div>
										</div>
										<div class="row wrap">
											<div className="col-xs-6 adjust-padding-left">
												<span className="details darkgray">Gender</span>
											</div>
											<div className="col-xs-6 align-right adjust-padding-right">{character.gender}</div>
										</div>
										<div class="row wrap">
											<div className="col-xs-6 adjust-padding-left">
												<span className="details darkgray">Origin</span>
											</div>
											<div className="col-xs-6 align-right adjust-padding-right">{character.origin.name}</div>
										</div>
										<div class="row wrap">
											<div className="col-xs-6 adjust-padding-left">
												<span className="details darkgray">Last Location</span>
											</div>
											<div className="col-xs-6 align-right adjust-padding-right">{character.location.name}</div>
										</div>
									</div>
								</div>
							))}
						</div>
					</div>
				</div>
			</div>
		) : null
	}
}
const mapDispatchToProps = dispatch => ({
  showProductsDispatch: (data) => {
		dispatch(loadProductInfo(data));
  },
});

const mapStateToProps = (state) => {
	return {
		characters: state && state.product,
	}
}

const SearchDetailStyles = withStyles(SearchDetail, styles);
const withConnect = connect(mapStateToProps, mapDispatchToProps);

const withRedux = initRedux({
	key: "SearchDetail",
	reducer: productDetailPageData,
	saga,
});

export default compose(
	withRedux,
	withConnect,
)(SearchDetailStyles);
