import {
    LOAD_PRODUCT_INFO_SUCCESS,
    FAILURE,
  } from './SearchDetail.constants';

  const getFailure = (state, action) => state.set('error', action.error);

  const loadProductDetail = (state, action) => state.set('product', action.data);

  const productDetailPageData = (state, action) => {
  switch (action.type) {
    case FAILURE:
            debugger;
      return getFailure(state, action);
    case LOAD_PRODUCT_INFO_SUCCESS:
        debugger;
      return loadProductDetail(state, action);
    default:
      return state;
  }
};

export default productDetailPageData;
